import com.github.spotbugs.snom.Effort

/*
 * This file was generated by the Gradle 'init' task.
 */

plugins {
    `java-library`
    `maven-publish`
    checkstyle
    id("com.github.spotbugs")
    id("com.azure.revapi-conventions")
}

repositories {
    mavenLocal()
    maven {
        url = uri("https://repo.maven.apache.org/maven2/")
    }

    maven {
        url = uri("https://oss.sonatype.org/content/repositories/snapshots/")
    }

    maven {
        url = uri("https://pkgs.dev.azure.com/azure-sdk/public/_packaging/azure-sdk-for-java/maven/v1")
    }
}

group = "com.azure"

sourceSets {
    main {
        java {
            exclude("module-info.java")
        }
    }
    test {
        java {
            setSrcDirs(listOf("src/test/java", "src/samples/java"))
        }
    }
    create("moduleInfo") {
        java {
            setSrcDirs(listOf("src/main/java"))
        }
    }
}

// This makes sure module-info output is included in dependent projects compilation.
// https://stackoverflow.com/questions/19936915/gradle-what-is-the-default-configuration-and-how-do-i-change-it
// https://github.com/gradle/gradle/blob/85d30969f4672bb2739550b4de784910a6810b7a/subprojects/plugins/src/main/java/org/gradle/api/plugins/JavaPlugin.java#L437
// https://discuss.gradle.org/t/why-does-a-compile-project-dependency-use-the-default-configuration-by-default/3497
configurations.create("azureProjectDependency") {
    extendsFrom(
        configurations.getByName("default"),
        configurations.getByName("moduleInfoImplementation"),
        configurations.getByName("moduleInfoRuntimeOnly")
    )
}

tasks.withType<JavaCompile> {
    modularity.inferModulePath.set(false)
    options.encoding = "UTF-8"
}

tasks.getByName<JavaCompile>("compileJava") {
    options.release.set(8)
}

val compileModuleInfoJava = tasks.getByName<JavaCompile>("compileModuleInfoJava") {
    options.release.set(9)
    doFirst {
        options.compilerArgs = listOf(
            "--module-path", configurations.compileClasspath.get().asPath,
            "-Xlint:-module",
            "-Xlint:-requires-transitive-automatic"
        )
    }
}

tasks.getByName("spotbugsModuleInfo") {
    enabled = false
}

val moduleInfoJar = tasks.register<Jar>("moduleInfoJar") {
    from(compileModuleInfoJava)
    include("module-info.class")
}

// https://melix.github.io/blog/2021/10/gradle-quickie-dependson.html
tasks.getByName<Jar>("jar") {
    from(compileModuleInfoJava) {
        include("module-info.class")
    }
}

tasks.named<Javadoc>("javadoc") {
    exclude("module-info.java", "**/impl/**", "**/implementation/**")
    modularity.inferModulePath.set(false)
}

java {
    withSourcesJar()
    withJavadocJar()
}

tasks.named<Test>("test") {
    useJUnitPlatform()
}

tasks.named<Copy>("processResources") {
    filesMatching("**.properties") {
        expand(mapOf("project" to mapOf("artifactId" to project.name, "version" to project.version)))
    }
}

checkstyle {
    configFile = file("${rootProject.projectDir}/eng/code-quality-reports/src/main/resources/checkstyle/checkstyle.xml")
    configProperties = mapOf("samedir" to "${rootProject.projectDir}/eng/code-quality-reports/src/main/resources/checkstyle")
    toolVersion = "9.2"
}

dependencies {
    checkstyle(project(":eng:code-quality-reports"))
    spotbugs("com.github.spotbugs:spotbugs:4.2.2")
}

spotbugs {
    effort.set(Effort.MAX)
    excludeFilter.set(file("${rootProject.projectDir}/eng/code-quality-reports/src/main/resources/spotbugs/spotbugs-exclude.xml"))
}

tasks.named("spotbugsTest") {
    enabled = false
}

val publishName: String by project
val publishDescription: String by project

publishing {
    publications {
        create<MavenPublication>("mavenJava") {
            artifactId = project.name
            from(components["java"])
            pom {
                name.set(publishName)
                description.set(publishDescription)
                url.set("https://github.com/Azure/azure-sdk-for-java")
                packaging = "jar"
                licenses {
                    license {
                        name.set("The MIT License (MIT)")
                        url.set("http://opensource.org/licenses/MIT")
                    }
                }
                scm {
                    connection.set("scm:git:https://github.com/Azure/azure-sdk-for-java.git")
                    developerConnection.set("scm:git:https://github.com/Azure/azure-sdk-for-java.git")
                    url.set("https://github.com/Azure/azure-sdk-for-java")
                }
            }
        }
    }
}

tasks.withType<GenerateModuleMetadata> {
    enabled = false
}

tasks.getByName<Jar>("jar") {
    into("META-INF/maven/${project.group}/${project.name}") {
        from(tasks.named("generatePomFileForMavenJavaPublication"))
        rename(".*", "pom.xml")
    }
}

tasks.register<Copy>("stageArtifacts") {
    from(tasks.named("generatePomFileForMavenJavaPublication")) {
        rename(".*", "${project.name}-${project.version}.pom")
    }
    from(tasks.named("jar"))
    from(tasks.named("javadocJar"))
    from(tasks.named("sourcesJar"))
    into("build/artifacts")
}

tasks.named("build") {
    dependsOn("stageArtifacts")
}

