content.query:
let runId = "{runId}";
let roleName = strcat("java-clientcore-http-", runId);
let metrics = customMetrics
| where timestamp >= {timeRange:start} and timestamp <= {timeRange:end}
| where cloud_RoleName == roleName;
let testSpans = dependencies
| where timestamp >= {timeRange:start} and timestamp <= {timeRange:end}
| where cloud_RoleName == roleName;
let errors = metrics
| where name == "test.run.duration"
| extend errorType = tostring(customDimensions["error.type"])
| summarize error_by_type=sum(valueCount) by errorType
| summarize test_errors=make_bag(bag_pack(errorType, error_by_type))
| evaluate narrow();
let runs = metrics 
| where name == "test.run.duration" 
| summarize successful_runs=sumif(valueCount, customDimensions["error.type"] == ""), total_runs=sum(valueCount)
| evaluate narrow();
let parameters = testSpans 
| where name == "before run"
| project params_pod=customDimensions["hostname"], params_scenarioName=customDimensions["scenarioName"], params_durationSec=customDimensions["durationSec"], params_concurrency=customDimensions["concurrency"], params_sync=customDimensions["sync"], params_httpClient=strcat(tostring(customDimensions["httpClientPackage"]), ":", tostring(customDimensions["httpClientPackageVersion"])), params_JRE=strcat(tostring(customDimensions["jreVendor"]), " ", tostring(customDimensions["jreVersion"]))
| evaluate narrow();
let actualDuration = metrics
| where name == "test.run.duration"
| summarize maxTs = max(timestamp), minTs = min(timestamp)
| project actual_durationSec=(maxTs-minTs)/1s
| evaluate narrow();
let avgThroughput = metrics 
| where name == "test.run.duration" 
| summarize throughputPerMin=sum(valueCount) by bin(timestamp, 1m) // in case AppInsights ingestion drops something
| summarize avg_throughtputPerSec=avg(throughputPerMin/60)
| evaluate narrow();
parameters 
| union runs, errors, actualDuration, avgThroughput
| project Property = Column, Value
