plugins {
    id("com.azure.java-conventions")
}

@CacheableRule
abstract class FixOkioRule : ComponentMetadataRule {

    override fun execute(context: ComponentMetadataContext) {
        // https://github.com/square/okio/issues/954#issuecomment-895403938
        // this renames "okio-jvm-2.8.0.jar" to "okio-2.8.0.jar"
        // otherwise module-info compilation fails.
        context.details.allVariants {
            withFiles {
                removeAllFiles()
                this.addFile("${context.details.id.name}-${context.details.id.version}.jar")
            }
        }
    }
}

dependencies {

    components {
        withModule<FixOkioRule>("com.squareup.okio:okio")
    }

    api(project(":sdk:core:azure-core", "azureProjectDependency"))
    api("com.squareup.okhttp3:okhttp:4.9.2")
    testImplementation(project(":sdk:core:azure-core", "azureProjectDependency"))
    testImplementation(project(":sdk:core:azure-core-test", "azureProjectDependency"))
    testImplementation(project(":sdk:core:azure-core-test", "tests"))
    testImplementation("io.projectreactor:reactor-test:3.4.16")
    testImplementation("org.junit.jupiter:junit-jupiter-api:5.8.2")
    testImplementation("org.junit.jupiter:junit-jupiter-engine:5.8.2")
    testImplementation("org.junit.jupiter:junit-jupiter-params:5.8.2")
    testImplementation("com.github.tomakehurst:wiremock-standalone:2.24.1")
    testImplementation("org.mockito:mockito-core:4.0.0")
}

description = "Microsoft Azure OkHttp HTTP Client Library"
