/*
 * This file was generated by the Gradle 'init' task.
 */

plugins {
    id 'com.azure.java-conventions'
}

dependencies {
    testImplementation project(':azure-core')
    testImplementation project(':azure-core')
    testImplementation 'com.fasterxml.jackson.core:jackson-annotations:2.13.2'
    testImplementation 'com.fasterxml.jackson.core:jackson-core:2.13.2'
    testImplementation 'com.fasterxml.jackson.core:jackson-databind:2.13.2.2'
    testImplementation 'com.fasterxml.jackson.datatype:jackson-datatype-jsr310:2.13.2'
    testImplementation 'com.fasterxml.jackson.dataformat:jackson-dataformat-xml:2.13.2'
    testImplementation 'org.junit.jupiter:junit-jupiter-api:5.8.2'
    testImplementation 'org.junit.jupiter:junit-jupiter-engine:5.8.2'
    testImplementation 'org.junit.jupiter:junit-jupiter-params:5.8.2'
    testImplementation 'io.projectreactor:reactor-test:3.4.16'
    testImplementation 'com.github.tomakehurst:wiremock-standalone:2.24.1'
    testImplementation 'org.hamcrest:hamcrest-library:2.2'
    testImplementation 'org.mockito:mockito-core:4.0.0'
    testImplementation 'org.openjdk.jmh:jmh-core:1.22'
    testImplementation 'org.openjdk.jmh:jmh-generator-annprocess:1.22'
    testImplementation 'javax.json:javax.json-api:1.1.4'
}

description = 'Microsoft Azure Java Core Jackson Tests Library'

java {
    withJavadocJar()
}
