/*
 * This file was generated by the Gradle 'init' task.
 */

plugins {
    id 'com.azure.java-conventions'
}

dependencies {
    implementation project(':azure-core')
    implementation project(':azure-core-http-netty')
    implementation project(':azure-core-http-okhttp')
    implementation project(':perf-test-core')
    implementation 'com.github.tomakehurst:wiremock-standalone:2.24.1'
    testImplementation 'org.junit.jupiter:junit-jupiter-api:5.8.2'
    testImplementation 'org.junit.jupiter:junit-jupiter-engine:5.8.2'
    testImplementation 'org.junit.jupiter:junit-jupiter-params:5.8.2'
    testImplementation 'io.projectreactor:reactor-test:3.4.16'
}

description = 'azure-core-perf'

java {
    withJavadocJar()
}
