content.query:
let runId = "{runId}";
let roleName = strcat("java-template-", runId);
let metrics = customMetrics
| where timestamp >= {timeRange:start} and timestamp <= {timeRange:end}
| where cloud_RoleName == roleName;
let testSpans = dependencies
| where timestamp >= {timeRange:start} and timestamp <= {timeRange:end}
| where cloud_RoleName == roleName;
let errors = metrics
| where name == "test.run.duration"
| extend errorType = tostring(customDimensions["error.type"])
| summarize error_by_type=sum(valueCount) by errorType
| summarize test_errors=make_bag(bag_pack(errorType, error_by_type))
| evaluate narrow();
let runs = metrics 
| where name == "test.run.duration" 
| summarize successful_runs=sumif(valueCount, customDimensions["error.type"] == ""), total_runs=sum(valueCount)
| evaluate narrow();
let parameters = testSpans 
| where name == "before run"
| project params_pod=customDimensions["hostname"], params_scenarioName=customDimensions["scenarioName"], params_durationMin=customDimensions["durationMin"], params_concurrency=customDimensions["concurrency"], params_sync=customDimensions["sync"], params_httpClient=customDimensions["httpClientProvider"]
| evaluate narrow();
parameters 
| union runs, errors
| project Property = Column, Value
